import { useState, useEffect, useRef } from 'react'
import Button from '@material-ui/core/Button'
import TextField from '@material-ui/core/TextField'
import io from 'socket.io-client'
import 'typeface-roboto'

const socket = io('https://xkeith.tech:3000')

const Index = () => {
  const [data, setData] = useState([])
  const [message, setMessage] = useState('')
  const chatRef = useRef(null)
  const list = data.map((val, index) => <li key={index}>{val}</li>)

  const handleMessage = e => {
    e.preventDefault()
    setMessage(e.target.value)
  }
  const handleSend = () => {
    if (message !== '') {
      socket.emit('chat message', message)
      setMessage('')
    }
  }
  const handleKeyDown = e => {
    if (e.which === 13) {
      handleSend()
    }
  }

  useEffect(() => {
    socket.on('chat message', msg => {
      const { current } = chatRef
      setData([...data, msg])
      // make scrollbar to the bottom
      current.scrollTop = current.scrollHeight
    })
    // must remove the socket when unmounted
    return () => {
      socket.off('chat message')
    }
  })

  return (
    <>
      <div className="container">
        <div className="main">
          <div className="chat" ref={chatRef}>
            <ul className="message">{list}</ul>
          </div>
          <div className="input">
            <TextField
              className="text"
              value={message}
              onChange={handleMessage}
              onKeyDown={handleKeyDown}
            />
            <Button onClick={handleSend} className="send">
              SEND
            </Button>
          </div>
        </div>
      </div>

      <style jsx>{`
        body {
          margin: 0px;
        }
        .container {
          display: flex;
          box-sizing: border-box;
        }
        .main {
          box-sizing: border-box;
          margin: auto;
          border: 1px black solid;
          border-radius: 10px;
          height: 90vh;
          width: 70%;
        }
        .chat {
          width: 100%;
          height: 93%;
          border-radius: 10px 10px 0 0;
          background-color: pink;
          overflow: auto;
        }
        .input {
          width: 100%;
          height: 7%;
          border-radius: 0 0 10px 10px;
          border: 1px burlywood solid;
        }
        .text {
          width: 70%;
          height: 80%;
        }
        .send {
          width: 25%;
          height: 80%;
          margin-left: 10px;
          background: black;
        }
      `}</style>
      <style jsx global>{`
        .message {
          list-style-type: none;
          margin: 0;
          padding: 10px;
        }
        .message li:nth-child(odd) {
          background: beige;
        }
      `}</style>
    </>
  )
}

export default Index
