const Index = () => {
  const data = ['mess1', 'mess2', 'mess3', 'mess4'].map(val => <li>{val}</li>);
  return (
    <>
      <div className="container">
        <div className="main">
          <div className="chat"></div>
          <ul className="message"></ul>
          <div className="input">
            <input />
            <button>SEND</button>
          </div>
        </div>
      </div>

      <style jsx>{`
        body {
          margin: 0px;
        }
        .container {
          display: flex;
          box-sizing: border-box;
        }
        .main {
          box-sizing: border-box;
          margin: auto;
          border: 1px black solid;
          border-radius: 10px;
          height: 90vh;
          width: 70%;
        }
        .chat {
          width: 100%;
          height: 93%;
          border-radius: 10px 10px 0 0;
          background-color: pink;
        }
        .input {
          width: 100%;
          height: 7%;
          border-radius: 0 0 10px 10px;
          border: 1px burlywood solid;
        }
        input {
          width: 70%;
          height: 80%;
        }
        button {
          width: 25%;
          height: 80%;
          margin-left: 10px;
        }
      `}</style>
    </>
  );
};

export default Index;
